import React, { useState, useEffect } from 'react' 
//import supersData from '../src/data/batman.json'
//import supersData2 from '../src/data/ironman.json'
import axios from 'axios'
import {ListItem, ListItemAvatar, ListItemText, ListItemSecondaryAction, Checkbox, Avatar} from '@material-ui/core'
import { Card, CardActionArea, CardContent, CardMedia, Typography} from '@material-ui/core'
import LazyLoad from 'react-lazyload'
import batman from './assets/images/batman.jpg' 












/*

// Batman

//const superheroes = supersData.results[0].members 

const heroName = superheroes.reduce((acc, superhero) => {
    //const powers = acc.durability === "55" ? 'Batman' : 'Superman'
    return acc.name > superhero.name ? acc : superhero

}) 

const heroStats = superheroes.reduce((acc, superhero) => {
    //const powers = acc.durability === "55" ? 'Batman' : 'Superman'
    return acc.id > superhero.id ? acc : superhero

}) 


// Ironman

//const superheroes2 = supersData2.results[0].members 


const heroName2 = superheroes2.reduce((acc, superhero2) => {
    //const powers = acc.durability === "55" ? 'Batman' : 'Superman'
    return acc.name > superhero2.name ? acc : superhero2

}) 


const heroStats2 = superheroes2.reduce((acc, superhero2) => {
    //const powers = acc.durability === "55" ? 'Batman' : 'Superman'
    return acc.id > superhero2.id ? acc : superhero2

}) 


//ternary operator: condition ? true  : false
*/



const [superheroData, setSuperheroData] = useState({ heroes: [] })
const [loading, setLoading] = useState({ loading: false})



const fetchReps = () => {
    setSuperheroData({loading: true})
    axios
    .get('https://superheroapi.com/api', {
    headers: {'x-api-key': process.env.REACT_APP_SUPERHERO_VILLIAN_API_KEY}
    })
    .then(function (response) {
    console.log(response.data)
    setSuperheroData({
        loading: false, 
        heroes: response.data.results[0].length,
    })
    }).catch(function(error){
        console.log(error)
    })

    }

    useEffect(() => {
    fetchReps()
    }, [])








function Superheroes(){
return (
<div className ="superheroDetail">
    <h1>Superheroes</h1>


    {
        superheroData.map((superhero) => {
            return (
<div>
    <h1>{superheroData.heroes.length} Heroes</h1>
            <ListItem button> 
            <ListItemAvatar>
            <LazyLoad once={true} alt="...">
            <Avatar 
            alt={'Avatar'}
            src={batman}
            />
            </LazyLoad>
            </ListItemAvatar>
            <ListItemText primary={`${superheroData.name}`}/>
            <ListItemSecondaryAction>
            <Checkbox edge="end"/>

            </ListItemSecondaryAction>
            </ListItem>

<Card className="card">
<CardActionArea>
  <CardMedia className="CardImage"
    image={batman}
    title="Contemplative Reptile"
  />
  <CardContent>
    <Typography gutterBottom variant="h5" component="h2">
    <h1>Name: {superheroData.name}</h1>
    </Typography>
    <Typography variant="body2" color="textSecondary" component="p">
      <p>ID: {superheroData.id}</p>
    </Typography>
  </CardContent>
</CardActionArea>
</Card>

</div>




        )
        }
        )









    }

</div>


)
}






  export default Superheroes
