import React from 'react' 
import supersData from '../src/data/batman.json'
import { Button, Card, CardActionArea, CardActions, CardContent, CardMedia, Typography} from '@material-ui/core'
import batman from './assets/images/batman.jpg' 



const superheroes = supersData.results[0].members 

const heroName = superheroes.reduce((acc, superhero) => {
    //const powers = acc.durability === "55" ? 'Batman' : 'Superman'
    return acc.name > superhero.name ? acc : superhero

}) 


const heroStats = superheroes.reduce((acc, superhero) => {
    //const powers = acc.durability === "55" ? 'Batman' : 'Superman'
    return acc.name > superhero.name ? acc : superhero

}) 


//ternary operator: condition ? true  : false


function Superheroes(){
return(
<div className ="superheroDetail">
    <h1>Superheroes</h1>


    {
        superheroes.map(superhero => {
            return ( <Card className="card">
                <CardActionArea>
                  <CardMedia className="CardImage"
                    image={batman}
                    title="Contemplative Reptile"
                  />
                  <CardContent>
                    <Typography gutterBottom variant="h5" component="h2">
                    <h1>Name: {heroName.name}</h1>
                    </Typography>
                    <Typography variant="body2" color="textSecondary" component="p">
                      <p>{heroStats.powerstats}</p>
                    </Typography>
                  </CardContent>
                </CardActionArea>
                <CardActions>
                  <Button size="small" color="primary">
                    Share
                  </Button>
                  <Button size="small" color="primary">
                    Learn More
                  </Button>
                </CardActions>
              </Card>
            )
        })
    }

</div>


)

}




  export default Superheroes
