import React from 'react' 
import supersData from '../src/data/batman.json'
import Card  from '@material-ui/core';
import CardActionArea from '@material-ui/core/CardActionArea';
import CardActions from '@material-ui/core/CardActions';
import CardContent from '@material-ui/core/CardContent';
import CardMedia from '@material-ui/core/CardMedia';
import Button from '@material-ui/core/Button';
import Typography from '@material-ui/core/Typography';



const superheroes = supersData.results[0].members 


const heroName = superheroes.reduce((acc, superhero) => {
    //const powers = acc.durability === "55" ? 'Batman' : 'Superman'
    return acc.name > superhero.name ? acc : superhero

}) 

const superheroID = superheroes.filter(superhero => superhero.id === 69 )

const superheroImage = superheroes.filter(superhero => superhero.image.url === 100 )

//ternary operator: condition ? true  : false


function Superheroes(){
return(
<div className ="superheroDetail">
    <h1>Superheroes</h1>
    <h1>Name: {heroName.name}</h1>
    <h1>Name: {superheroImage.image}</h1>
    <h1>ID: {superheroID.id}</h1>
    {
        superheroes.map(superhero => {
            return (
                <p>{superhero.id}</p>
            )

        })
    }

    {
        superheroes.map(superhero => {
            return ( <Card>
                <CardActionArea>
                  <CardMedia
                    width="100px"
                    height="200px"
                    image="/static/images/cards/contemplative-reptile.jpg"
                    title="Contemplative Reptile"
                  />
                  <CardContent>
                    <Typography gutterBottom variant="h5" component="h2">
                      Lizard
                    </Typography>
                    <Typography variant="body2" color="textSecondary" component="p">
                      Lizards are a widespread group of squamate reptiles, with over 6,000 species, ranging
                      across all continents except Antarctica
                    </Typography>
                  </CardContent>
                </CardActionArea>
                <CardActions>
                  <Button size="small" color="primary">
                    Share
                  </Button>
                  <Button size="small" color="primary">
                    Learn More
                  </Button>
                </CardActions>
              </Card>
            )
        })
    }

</div>


)

}




  export default Superheroes
