import React from 'react' 
import supersData from '../src/data/batman.json'

const superheroes = supersData.results[0].members 


const stats = superheroes.reduce((acc, superhero) => {
    //const powers = acc.durability === "55" ? 'Batman' : 'Superman'
    return acc.stats > superhero.stats ? acc : superhero

}) 

const abilities = superheroes.filter(superhero => superhero.powerstats === 81 )

//ternary operator: condition ? true  : false


function Superheroes(){
return(
<div>
    <h1>Superheroes</h1>
    <h2>Durability: {stats.durability}</h2>
    <h2>Intelligence: {abilities.powerstats}</h2>
    {
        superheroes.map(superhero => {
            return (
                <p>{superhero.powerstats}</p>
            )

        })
    }

    {
        superheroes.map(superhero => {
            return (<p>
                {superhero.name}
            </p>
            )
        })
    }

</div>


)

}

export default Superheroes
