  axios
  .get(`https://gateway.marvel.com:443/v1/public/characters`, {
    headers: {'x-api-key': process.env.REACT_APP_MARVEL_API_KEY}
  })
  .then(function (response) {
  console.log(response.data) 
  setSuperheroData({
      loading: false, 
      heroes: response.data.results
