              <SuperheroView
              heroes={comic}
              key={comic.items.name + comic.available}

              >

              </SuperheroView>
              </LazyLoad>
            )
            })}
            </List>

            <Card className="superheroCard">
      <CardActionArea>
      <ListItemAvatar>
            <LazyLoad once={true} alt="...">
            <Avatar 
            alt={'Avatar'}
            placeholder={<Loading />}
            src={batman}
            />
            </LazyLoad>
            </ListItemAvatar>
        <CardContent>
          <Typography gutterBottom variant="h5" component="h2">
          {superheroData.heroes.name}
          {superheroData.heroes.description}
          {superheroData.heroes.id}
          </Typography>
          <Typography variant="body2" color="textSecondary" component="p">
            Lizards are a widespread group of squamate reptiles, with over 6,000 species, ranging
            across all continents except Antarctica
          </Typography>
        </CardContent>
      </CardActionArea>
      <CardActions>
        <Button size="small" color="primary">
          Share
        </Button>
        <Button size="small" color="primary">
          Learn More
        </Button>
      </CardActions>
    </Card>

</div>
  )
}

