import React from 'react' 
import supersData from '../src/data/batman.json'

const superheroes = supersData.results[0].members 
const members = superheroes.reduce((acc, superhero) => {
    const powers = acc.durability === "55" ? 'Batman' : 'Superman'
    return acc.members > superhero.members ? { ...acc, powers } : { ...superhero, powers} 

}) 

const biography = superheroes.filter(superhero => superhero.aliases === "Batman II")

//ternary operator: condition ? true  : false


function Superheroes(){
return(
<div>
    <h1>Superheroes</h1>
    <h2>Durability: {members.durability}</h2>
    <h2>Aliases: {biography.aliases}</h2>
    {
        biography.map(superhero => {
            return (
                <p>{superhero.aliases}</p>
            )

        })
    }

    {
        superheroes.map(superhero => {
            return (<p>
                {superhero.name}
            </p>
            )
        })
    }

</div>


)

}

export default Superheroes
