import React, { useState, useEffect } from 'react' 
//import supersData from '../src/data/batman.json'
//import supersData2 from '../src/data/ironman.json'
//import SuperheroesList from '.SuperheroesList'
import axios from 'axios'
//import {List} from '@material-ui/core'
import {LazyLoad} from 'react-lazyload'
import {ListItem, ListItemAvatar, ListItemText, ListItemSecondaryAction, Checkbox, Avatar} from '@material-ui/core'
import {Card, CardActionArea, CardContent, CardMedia, Typography} from '@material-ui/core'
import batman from './assets/images/batman.jpg' 



/*
const Loading = () => (
  <div>
    <h4>Loading...</h4>
  </div>
)

*/

//const [superData, setSuperData] = useState({superHeroes: [] })
//const [loading, setLoading] = useState({ loading: false})




const SuperheroView = (props) => {
const superhero = props.hero
const superheroName = superhero.name 



const [superheroData, setSuperheroData] = useState({ loading: false, heroes: [] })


const fetchReps = async () => {
  setSuperheroData({loading: true})
  axios.get('https://superheroapi.com/api/10163163232998504/search/batman')
  .then(function (response) {
  console.log(response); 
  setSuperheroData({
      loading: false, 
      heroes: response.data.results.powerstats,
  }).catch(function (error){ 
      console.log(error)
  })
  })
  }

  useEffect(() => {
  fetchReps()
  }, [])



return (
<div>

<ListItem button> 
<ListItemAvatar>
            <LazyLoad once={true} alt="...">
            <Avatar 
            alt={'Avatar'}
            src={batman}
            />
            </LazyLoad>
            </ListItemAvatar>
            <ListItemText primary={`${superheroData.name}`}/>
            <ListItemSecondaryAction>
            <Checkbox edge="end"/>

            </ListItemSecondaryAction>
            </ListItem>

            <Card className="card">
<CardActionArea>
  <CardMedia className="CardImage"
    image={batman}
    title="Contemplative Reptile"
  />
  <CardContent>
    <Typography gutterBottom variant="h5" component="h2">
    <h1>Name: {superheroData.name}</h1>
    </Typography>
    <Typography variant="body2" color="textSecondary" component="p">
      <p>ID: {superheroData.id}</p>
    </Typography>
  </CardContent>
</CardActionArea>
</Card>

</div>
  )

}



  export default SuperheroView
